import { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [apiData, setApiData] = useState({
    id:'', advice:''
  });

  const getApiData = async() => {
    return await axios.get('https://api.adviceslip.com/advice')
    // .then(res => res.data.slip)
      .then(res => {
        setApiData({
          id: res.data.slip.id,
          advice: res.data.slip.advice
        })
      })
  };

  useEffect(() => {
    console.log(getApiData());
  }, []);

  // console.log(apiData)

  return (
    <div className="App">
      <main>
        <section>
          <h4>ADVICE # {apiData.id}</h4>
          <div></div>
        </section>
      </main>
    </div>
  );
}

export default App;
